import React, { useState, useEffect } from 'react';
import './media-queries.css';
import { NavLink } from 'react-router-dom';

function Menu() {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [navbarClass, setNavbarClass] = useState('black-bg visible');
  const [prevScrollY, setPrevScrollY] = useState(0);


  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY < prevScrollY) {
        setNavbarClass('black-bg visible');
      } else {
        setNavbarClass('transparent slide-up');
      }

      setPrevScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollY]);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
      };
    return (
        <>
        <button className={`menu ${navbarClass}`} onClick={toggleMenu}>Menu</button>
            <div className={`mobile-menu ${isMenuOpen ? 'open' : 'close'}`}>
                <div className='mobie-content'>
            <NavLink to="/features" className="link">
              Features
            </NavLink>
            <NavLink to="/ourplan" className="link">
              Our Plan
            </NavLink>
            <NavLink to="/feedback" className="link">
              Feedback
            </NavLink>
            <NavLink to="/contact" className="link">
              Contact Us
            </NavLink>
            <NavLink to="/donation" className="link">
              Donations
            </NavLink>
                </div>
            </div>
            </>
    );
}
export default Menu;
