        <><h1>Hello! {username}</h1>
        <form onSubmit={nameSubmit}>
            <label htmlFor="firstName">
                FirstName:
                <input
                    onChange={(e) => setFirstName(e.target.value)}
                ></input>
            </label>
            <button className="submit">Submit</button>
        </form></>
    )
}

export default Account
