import React, { useState, useEffect } from 'react';
import './media-queries.css';
import { NavLink } from 'react-router-dom';
import Logo from './logo';

function Menu() {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [navbarClass, setNavbarClass] = useState('black-bg visible');
  const [prevScrollY, setPrevScrollY] = useState(0);


  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY < prevScrollY) {
        setNavbarClass('down');
      } else {
        setNavbarClass('up');
      }

      setPrevScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollY]);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
      };
    return (
        <>
        <span className={`menu ${navbarClass}`} onClick={toggleMenu}>Menu</span>
            <div className={`mobile-menu ${isMenuOpen ? 'open' : 'close'}`}>
                <div className='mobie-content'>
                    <Logo />
                <NavLink onClick={toggleMenu} className="backlink">
              Back
            </NavLink>
            <div className="mobile-links">
            <NavLink to="/login" className="link">
              Log in
            </NavLink>
            <NavLink to="/singup" className="link">
              Sign up
            </NavLink>
            <NavLink to="/features" className="link">
              Features
            </NavLink>
            <NavLink to="/ourplan" className="link">
              Our Plan
            </NavLink>
            <NavLink to="/feedback" className="link">
              Feedback
            </NavLink>
            <NavLink to="/contact" className="link">
              Contact Us
            </NavLink>
            <NavLink to="/donation" className="link">
              Donations
            </NavLink>
                </div>
                </div>
            </div>
            </>
    );
}
export default Menu;
