import logo from './logo.svg';
import React, { useState, useEffect } from 'react';
import './media-queries.css';

function Navbar() {
  const [navbarClass, setNavbarClass] = useState('black-bg visible');
  const [prevScrollY, setPrevScrollY] = useState(0);
  const [isMenuOpen, setIsMenuOpen] = useState(true);


  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY < prevScrollY) {
        setNavbarClass('black-bg visible');
      } else {
        setNavbarClass('transparent slide-up');
      }

      setPrevScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollY]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };
  return (
    <>
    <nav className={`navbar ${navbarClass}`}>
      <div className="navbar-content">
        <div className="desktop-menu">
    <button onClick={toggleMenu}>Menu</button>
    <li className={`mobile-menu ${isMenuOpen ? 'open' : ''}`}>
      <ul>
        <li className="align-content">
        <a href='#'>Log in</a>
        <a href='a' className='signup'>Sign up</a>
        <a onClick={toggleMenu}>back</a>
        </li>
        </ul>
      </li>
      </div>
      </div>
    </nav>
      </>

  );
}

export default Navbar;
