import logo from './logo.svg';
import React, { useState, useEffect } from 'react';
import './media-queries.css';
import styled from 'styled-components';
import {NavLink as Link } from 'react-router-dom';

function Navbar() {
  const [navbarClass, setNavbarClass] = useState('black-bg visible');
  const [prevScrollY, setPrevScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY < prevScrollY) {
        setNavbarClass('black-bg visible');
      } else {
        setNavbarClass('transparent slide-up');
      }

      setPrevScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollY]);
  return (
    <Nav className={`navbar ${navbarClass}`}>
      <div className="navbar-content">

        <div className="desktop-menu">
          {/* Desktop menu content, e.g., login, signup */}
          <Bars />
          <NavMenu>
            <NavLink to="/features" className="desktop-menu">
              Features
            </NavLink>
            <NavLink to="/ourplan" className="desktop-menu">
              Our Plan
            </NavLink>
            <NavLink to="/contact" className="desktop-menu">
              Contact Us
            </NavLink>
            <NavLink to="/donation" className="desktop-menu">
              Donations
            </NavLink>
          </NavMenu>
          <NavBtns>
            <NavBtnLink to="/login">
              Log in
            </NavBtnLink>
            <NavBtnLink to="/signup" className='signup'>
              Sign up
            </NavBtnLink>
          </NavBtns>
        </div>
      </div>
    </Nav>
  );
}

export default Navbar;
