import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';

function Navbar() {
    const [navbarColor, setNavbarColor] = useState('transparent');

    useEffect(() => {
      const changeNavbarColor = () => {
        if (window.scrollY >= 50) {
          setNavbarColor('black'); // Change the color based on your preference
        } else {
          setNavbarColor('transparent');
        }
      };

      window.addEventListener('scroll', changeNavbarColor);

      // Clean up the event listener on unmount
      return () => {
        window.removeEventListener('scroll', changeNavbarColor);
      };
    }, []);
    const navbarStyles = {
        backgroundColor: navbarColor,
        transition: 'background-color 0.3s ease', // Adding transition property inline
        height: '13vh',
        position: 'sticky',
        top: 0,
        // Media query for screens up to 768px (typical mobile width)
        '@media (max-width: 900px)': {
          height: '8vh', // Adjust the height for mobile
          // Other mobile-specific styles
        }
      };
    return (
      <nav className="navbar" style={ navbarStyles }>
        {/* Navbar content */}
      </nav>
    );
  }

  export default Navbar;
