import logo from './logo.svg';
import React, { useState, useEffect } from 'react';
import './media-queries.css';
function Navbar() {
  const [navbarVisible, setNavbarVisible] = useState(true);
  const [navbarClass, setNavbarClass] = useState('black-bg');
  const [prevScrollY, setPrevScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY < prevScrollY) {
        setNavbarVisible(true);
        setNavbarClass('black-bg');
      } else {
        setNavbarVisible(false);
        setNavbarClass('transparent');
      }

      setPrevScrollY(currentScrollY);
    };

    const debouncedHandleScroll = debounce(handleScroll, 100); // Adjust the debounce delay as needed

    window.addEventListener('scroll', debouncedHandleScroll);

    // Clean up the event listener on unmount
    return () => {
      window.removeEventListener('scroll', debouncedHandleScroll);
    };
  }, [prevScrollY]);

  return (
    <nav className={`navbar ${navbarClass} ${navbarVisible ? 'visible' : 'slide-up'}`}>
      {/* Navbar content */}
    </nav>
  );
}

// Debounce function to limit the frequency of calls to a function
function debounce(func, delay) {
  let timeoutId;
  return function () {
    const context = this;
    const args = arguments;
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      func.apply(context, args);
    }, delay);
  };
}

export default Navbar;
