import logo from './logo.svg';
import React, { useState, useEffect } from 'react';
import './media-queries.css';
function Navbar() {
  const [navbarClass, setNavbarClass] = useState('black-bg visible'); // Start with visible class
  const [prevScrollY, setPrevScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY < prevScrollY) {
        setNavbarClass('black-bg visible'); // Toggle the visible class when scrolling up
      } else {
        setNavbarClass('transparent slide-up'); // Apply the slide-up class for sliding up animation
      }

      setPrevScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    // Clean up the event listener on unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollY]);

  return (
    <nav className={`navbar ${navbarClass}`}>
      {/* Navbar content */}
    </nav>
  );
}

export default Navbar;
