import logo from './logo.svg';
import React, { useState, useEffect } from 'react';
import './media-queries.css';
function Navbar() {
  const [navbarClass, setNavbarClass] = useState('black-bg visible');
  const [prevScrollY, setPrevScrollY] = useState(0);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY < prevScrollY) {
        setNavbarClass('black-bg visible');
      } else {
        setNavbarClass('transparent slide-up');
      }

      setPrevScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollY]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className={`navbar ${navbarClass}`}>
      <div className="navbar-content">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
          <span className="logo-text">InvestrTies</span>
        </div>
        <button className="menu" onClick={toggleMenu}>
          Menu
        </button>
        <div className={`mobile-menu ${isMenuOpen ? 'open' : ''}`}>
          <button className="back-button" onClick={toggleMenu}>
            Back
          </button>
          <span>Login</span>
          <span className="signup">Signup</span>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
