import logo from './logo.svg';
import React, { useState, useEffect } from 'react';
import './media-queries.css';

function Navbar() {
  const [navbarClass, setNavbarClass] = useState('black-bg visible');
  const [prevScrollY, setPrevScrollY] = useState(0);
  const [isMenuOpen, setIsMenuOpen] = useState(false);


  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY < prevScrollY) {
        setNavbarClass('black-bg visible');
      } else {
        setNavbarClass('transparent slide-up');
      }

      setPrevScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollY]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };
  return (
    <nav className={`navbar ${navbarClass}`}>
      <div className="navbar-content">   
        <div className="desktop-menu">
        <button className="menu" onClick={toggleMenu}>Menu</button>
        <div className={`mobile-menu ${isMenuOpen ? 'open' : ''}`}>
        <button onClick={toggleMenu}>back</button>
          <span>Log in</span>
          <span className='signup'>Sign up</span>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
